#Libraryimport pandas as pdimport numpy as npimport matplotlib.pyplot as plt#uploading datarev = pd.read_csv("rev.csv")a = rev.iloc[:,1:2]b= rev.iloc[:,2:]A = a.valuesB = b.values#Lenear regressionfrom sklearn.linear_model import LinearRegressionlin=LinearRegression()lin.fit(A,B)plt.scatter(A,B , color="blue")plt.plot(a, lin.predict(a) , color="red")plt.show()#polynomial regression#polynomial dönüşümfrom sklearn.preprocessing import PolynomialFeaturespol_reg=PolynomialFeatures(degree=4)x_pol=pol_reg.fit_transform(A)print(x_pol)#polynomial regression uygulanmasılin_regres=LinearRegression()lin_regres.fit(x_pol, B)plt.scatter(A,B)plt.plot(A,lin_regres.predict(x_pol))#plt.show()print(lin_regres.predict(pol_reg.fit_transform([[11]])))